import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { 
  getAuth, 
  signInWithPhoneNumber,
  RecaptchaVerifier,
  signOut,
  onAuthStateChanged 
} from 'firebase/auth';
import { 
  getFirestore, 
  collection, 
  getDocs, 
  doc, 
  updateDoc, 
  deleteDoc,
  addDoc,
  getDoc
} from 'firebase/firestore';
import { getStorage } from 'firebase/storage';
import { Users, FileText, Bell, LogOut, Trash2, Edit, Plus, X, Check, Phone } from 'lucide-react';

// Конфигурация Firebase (замените на свою)
const firebaseConfig = {
  apiKey: "AIzaSyDk4pU2oEaRpx_hajUzIVrzzpSR0GtcwMA",
  authDomain: "ethnogram-1cd0f.firebaseapp.com",
  projectId: "ethnogram-1cd0f",
  storageBucket: "ethnogram-1cd0f.appspot.com",
  messagingSenderId: "316785912727",
  appId: "1:316785912727:web:43818038dc12d5af3532d2",
  measurementId: "G-0L6Y649070"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);

export default function AdminPanel() {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('posts');

  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [confirmationResult, setConfirmationResult] = useState(null);
  const [loginError, setLoginError] = useState('');
  const [loginStep, setLoginStep] = useState('phone'); // 'phone' или 'code'

  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [editingPost, setEditingPost] = useState(null);
  const [showAddPost, setShowAddPost] = useState(false);
  const [newPost, setNewPost] = useState({ title: '', content: '', imageUrl: '' });
  const [notification, setNotification] = useState({ title: '', message: '' });
  const [showNotificationForm, setShowNotificationForm] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      setUser(currentUser);
      if (currentUser) {
        const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
        if (userDoc.exists() && userDoc.data().isAdmin) {
          setIsAdmin(true);
          loadData();
        } else {
          setIsAdmin(false);
        }
      } else {
        setIsAdmin(false);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user && !loading) {
      setupRecaptcha();
    }
  }, [user, loading]);

  const setupRecaptcha = () => {
    if (!window.recaptchaVerifier) {
      window.recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
        size: 'invisible',
        callback: () => {
          console.log('reCAPTCHA verified');
        }
      });
    }
  };

  const loadData = async () => {
    try {
      const postsSnapshot = await getDocs(collection(db, 'posts'));
      setPosts(postsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));

      const usersSnapshot = await getDocs(collection(db, 'users'));
      setUsers(usersSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const handleSendCode = async () => {
    setLoginError('');

    if (!phoneNumber) {
      setLoginError('Введите номер телефона');
      return;
    }

    // Форматируем номер для Firebase (должен начинаться с +)
    let formattedPhone = phoneNumber.trim();
    if (!formattedPhone.startsWith('+')) {
      formattedPhone = '+' + formattedPhone;
    }

    try {
      const appVerifier = window.recaptchaVerifier;
      const confirmation = await signInWithPhoneNumber(auth, formattedPhone, appVerifier);
      setConfirmationResult(confirmation);
      setLoginStep('code');
      setLoginError('');
    } catch (error) {
      console.error('Error sending code:', error);
      setLoginError('Ошибка отправки кода. Проверьте номер телефона.');

      // Сбросить recaptcha при ошибке
      if (window.recaptchaVerifier) {
        window.recaptchaVerifier.clear();
        window.recaptchaVerifier = null;
        setupRecaptcha();
      }
    }
  };

  const handleVerifyCode = async () => {
    setLoginError('');

    if (!verificationCode) {
      setLoginError('Введите код подтверждения');
      return;
    }

    try {
      await confirmationResult.confirm(verificationCode);
      // Пользователь автоматически войдет через onAuthStateChanged
    } catch (error) {
      console.error('Error verifying code:', error);
      setLoginError('Неверный код. Попробуйте снова.');
    }
  };

  const handleLogout = async () => {
    await signOut(auth);
    setLoginStep('phone');
    setPhoneNumber('');
    setVerificationCode('');
    setConfirmationResult(null);
  };

  const handleDeletePost = async (postId) => {
    if (window.confirm('Удалить этот пост?')) {
      await deleteDoc(doc(db, 'posts', postId));
      loadData();
    }
  };

  const handleUpdatePost = async (postId, updatedData) => {
    await updateDoc(doc(db, 'posts', postId), updatedData);
    setEditingPost(null);
    loadData();
  };

  const handleAddPost = async () => {
    if (!newPost.title || !newPost.content) {
      alert('Заполните все обязательные поля');
      return;
    }
    await addDoc(collection(db, 'posts'), {
      ...newPost,
      createdAt: new Date(),
      authorId: user.uid
    });
    setShowAddPost(false);
    setNewPost({ title: '', content: '', imageUrl: '' });
    loadData();
  };

  const handleToggleUserAdmin = async (userId, currentStatus) => {
    await updateDoc(doc(db, 'users', userId), {
      isAdmin: !currentStatus
    });
    loadData();
  };

  const handleSendNotification = async () => {
    if (!notification.title || !notification.message) {
      alert('Заполните все поля уведомления');
      return;
    }

    await addDoc(collection(db, 'notifications'), {
      title: notification.title,
      message: notification.message,
      createdAt: new Date(),
      sentBy: user.uid
    });

    setShowNotificationForm(false);
    setNotification({ title: '', message: '' });
    alert('Уведомление отправлено!');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center">
        <div className="text-white text-xl">Загрузка...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md">
          <div className="flex items-center justify-center mb-6">
            <div className="bg-blue-100 p-4 rounded-full">
              <Phone size={32} className="text-blue-600" />
            </div>
          </div>
          <h1 className="text-3xl font-bold text-slate-800 mb-2 text-center">Админ Панель</h1>
          <p className="text-slate-600 text-center mb-6">Вход по номеру телефона</p>

          <div id="recaptcha-container"></div>

          {loginStep === 'phone' ? (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Номер телефона (с кодом страны)
                </label>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="+79991234567"
                  onKeyDown={(e) => e.key === 'Enter' && handleSendCode()}
                  className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <p className="text-xs text-slate-500 mt-1">
                  Пример: +7 999 123 45 67
                </p>
              </div>

              {loginError && (
                <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg text-sm">
                  {loginError}
                </div>
              )}

              <button
                onClick={handleSendCode}
                className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition flex items-center justify-center gap-2"
              >
                <Phone size={20} />
                Отправить код
              </button>
            </div>
          ) : (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Код подтверждения
                </label>
                <input
                  type="text"
                  value={verificationCode}
                  onChange={(e) => setVerificationCode(e.target.value)}
                  placeholder="123456"
                  maxLength="6"
                  onKeyDown={(e) => e.key === 'Enter' && handleVerifyCode()}
                  className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-center text-2xl tracking-widest"
                />
                <p className="text-xs text-slate-500 mt-1 text-center">
                  Код отправлен на {phoneNumber}
                </p>
              </div>

              {loginError && (
                <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg text-sm">
                  {loginError}
                </div>
              )}

              <button
                onClick={handleVerifyCode}
                className="w-full bg-green-600 text-white py-3 rounded-lg font-semibold hover:bg-green-700 transition flex items-center justify-center gap-2"
              >
                <Check size={20} />
                Подтвердить
              </button>

              <button
                onClick={() => {
                  setLoginStep('phone');
                  setVerificationCode('');
                  setLoginError('');
                }}
                className="w-full text-slate-600 py-2 rounded-lg hover:bg-slate-100 transition"
              >
                Изменить номер
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  if (!isAdmin) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center">
        <div className="bg-white rounded-2xl shadow-2xl p-8 text-center">
          <h2 className="text-2xl font-bold text-red-600 mb-4">Доступ запрещен</h2>
          <p className="text-slate-600 mb-6">У вас нет прав администратора</p>
          <button
            onClick={handleLogout}
            className="bg-slate-600 text-white px-6 py-2 rounded-lg hover:bg-slate-700"
          >
            Выйти
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800">
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-white rounded-2xl shadow-2xl overflow-hidden">
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-6 flex justify-between items-center">
            <h1 className="text-3xl font-bold text-white">Админ Панель</h1>
            <button
              onClick={handleLogout}
              className="flex items-center gap-2 bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition"
            >
              <LogOut size={20} />
              Выйти
            </button>
          </div>

          <div className="flex border-b">
            <button
              onClick={() => setActiveTab('posts')}
              className={`flex items-center gap-2 px-6 py-4 font-semibold transition ${
                activeTab === 'posts'
                  ? 'bg-blue-50 text-blue-600 border-b-2 border-blue-600'
                  : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <FileText size={20} />
              Посты
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`flex items-center gap-2 px-6 py-4 font-semibold transition ${
                activeTab === 'users'
                  ? 'bg-blue-50 text-blue-600 border-b-2 border-blue-600'
                  : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Users size={20} />
              Пользователи
            </button>
            <button
              onClick={() => setActiveTab('notifications')}
              className={`flex items-center gap-2 px-6 py-4 font-semibold transition ${
                activeTab === 'notifications'
                  ? 'bg-blue-50 text-blue-600 border-b-2 border-blue-600'
                  : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Bell size={20} />
              Уведомления
            </button>
          </div>

          <div className="p-6">
            {activeTab === 'posts' && (
              <div>
                <div className="flex justify-between items-center mb-6">
                  <h2 className="text-2xl font-bold text-slate-800">Управление постами</h2>
                  <button
                    onClick={() => setShowAddPost(true)}
                    className="flex items-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                  >
                    <Plus size={20} />
                    Добавить пост
                  </button>
                </div>

                {showAddPost && (
                  <div className="bg-blue-50 p-6 rounded-xl mb-6">
                    <h3 className="text-xl font-semibold mb-4">Новый пост</h3>
                    <input
                      type="text"
                      placeholder="Заголовок"
                      value={newPost.title}
                      onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg mb-3"
                    />
                    <textarea
                      placeholder="Содержание"
                      value={newPost.content}
                      onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg mb-3 h-32"
                    />
                    <input
                      type="text"
                      placeholder="URL изображения (необязательно)"
                      value={newPost.imageUrl}
                      onChange={(e) => setNewPost({ ...newPost, imageUrl: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg mb-3"
                    />
                    <div className="flex gap-3">
                      <button
                        onClick={handleAddPost}
                        className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700"
                      >
                        Создать
                      </button>
                      <button
                        onClick={() => setShowAddPost(false)}
                        className="bg-slate-400 text-white px-6 py-2 rounded-lg hover:bg-slate-500"
                      >
                        Отмена
                      </button>
                    </div>
                  </div>
                )}

                <div className="space-y-4">
                  {posts.map((post) => (
                    <div key={post.id} className="border rounded-xl p-6 hover:shadow-lg transition">
                      {editingPost?.id === post.id ? (
                        <div>
                          <input
                            type="text"
                            value={editingPost.title}
                            onChange={(e) => setEditingPost({ ...editingPost, title: e.target.value })}
                            className="w-full px-4 py-2 border rounded-lg mb-3 font-semibold"
                          />
                          <textarea
                            value={editingPost.content}
                            onChange={(e) => setEditingPost({ ...editingPost, content: e.target.value })}
                            className="w-full px-4 py-2 border rounded-lg mb-3 h-24"
                          />
                          <div className="flex gap-3">
                            <button
                              onClick={() => handleUpdatePost(post.id, editingPost)}
                              className="flex items-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                            >
                              <Check size={18} />
                              Сохранить
                            </button>
                            <button
                              onClick={() => setEditingPost(null)}
                              className="flex items-center gap-2 bg-slate-400 text-white px-4 py-2 rounded-lg hover:bg-slate-500"
                            >
                              <X size={18} />
                              Отмена
                            </button>
                          </div>
                        </div>
                      ) : (
                        <div>
                          <h3 className="text-xl font-semibold text-slate-800 mb-2">{post.title}</h3>
                          <p className="text-slate-600 mb-4">{post.content}</p>
                          <div className="flex gap-3">
                            <button
                              onClick={() => setEditingPost(post)}
                              className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                            >
                              <Edit size={18} />
                              Редактировать
                            </button>
                            <button
                              onClick={() => handleDeletePost(post.id)}
                              className="flex items-center gap-2 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700"
                            >
                              <Trash2 size={18} />
                              Удалить
                            </button>
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'users' && (
              <div>
                <h2 className="text-2xl font-bold text-slate-800 mb-6">Управление пользователями</h2>
                <div className="grid gap-4">
                  {users.map((u) => (
                    <div key={u.id} className="border rounded-xl p-6 flex justify-between items-center hover:shadow-lg transition">
                      <div>
                        <h3 className="text-lg font-semibold text-slate-800">{u.phoneNumber || u.email || 'Без контакта'}</h3>
                        <p className="text-sm text-slate-500">ID: {u.id}</p>
                        <span className={`inline-block mt-2 px-3 py-1 rounded-full text-sm ${u.isAdmin ? 'bg-purple-100 text-purple-700' : 'bg-slate-100 text-slate-700'}`}>
                          {u.isAdmin ? 'Администратор' : 'Пользователь'}
                        </span>
                      </div>
                      <button
                        onClick={() => handleToggleUserAdmin(u.id, u.isAdmin)}
                        className={`px-6 py-2 rounded-lg font-semibold transition ${
                          u.isAdmin
                            ? 'bg-slate-600 text-white hover:bg-slate-700'
                            : 'bg-purple-600 text-white hover:bg-purple-700'
                        }`}
                      >
                        {u.isAdmin ? 'Снять права' : 'Сделать админом'}
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'notifications' && (
              <div>
                <h2 className="text-2xl font-bold text-slate-800 mb-6">Отправка уведомлений</h2>
                <button
                  onClick={() => setShowNotificationForm(!showNotificationForm)}
                  className="flex items-center gap-2 bg-purple-600 text-white px-6 py-3 rounded-lg hover:bg-purple-700 mb-6"
                >
                  <Bell size={20} />
                  Создать уведомление
                </button>

                {showNotificationForm && (
                  <div className="bg-purple-50 p-6 rounded-xl">
                    <h3 className="text-xl font-semibold mb-4">Новое уведомление</h3>
                    <input
                      type="text"
                      placeholder="Заголовок уведомления"
                      value={notification.title}
                      onChange={(e) => setNotification({ ...notification, title: e.target.value })}
                      className="w-full px-4 py-3 border rounded-lg mb-3"
                    />
                    <textarea
                      placeholder="Текст уведомления"
                      value={notification.message}
                      onChange={(e) => setNotification({ ...notification, message: e.target.value })}
                      className="w-full px-4 py-3 border rounded-lg mb-3 h-32"
                    />
                    <div className="flex gap-3">
                      <button
                        onClick={handleSendNotification}
                        className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700"
                      >
                        Отправить всем
                      </button>
                      <button
                        onClick={() => setShowNotificationForm(false)}
                        className="bg-slate-400 text-white px-6 py-2 rounded-lg hover:bg-slate-500"
                      >
                        Отмена
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
