import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { 
  getAuth, 
  signInWithPhoneNumber,
  RecaptchaVerifier,
  signOut,
  onAuthStateChanged 
} from 'firebase/auth';
import { 
  getFirestore, 
  collection, 
  getDocs, 
  doc, 
  updateDoc, 
  deleteDoc,
  addDoc,
  getDoc,
  query,
  orderBy
} from 'firebase/firestore';
import { getStorage } from 'firebase/storage';
import { Users, FileText, Bell, LogOut, Trash2, Edit, Plus, X, Check, Phone, Search, Filter, TrendingUp, Calendar, Image as ImageIcon, Mail } from 'lucide-react';

const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);

const POST_TYPES = {
  0: { label: 'ÐÐ¾Ð²Ð¾ÑÑ‚ÑŒ', color: 'bg-blue-100 text-blue-700', icon: 'ðŸ“°' },
  1: { label: 'Ð¡Ð¾Ð±Ñ‹Ñ‚Ð¸Ðµ', color: 'bg-green-100 text-green-700', icon: 'ðŸ“…' },
  2: { label: 'Ð ÐµÐºÐ»Ð°Ð¼Ð°', color: 'bg-amber-100 text-amber-700', icon: 'ðŸ“¢' }
};

export default function AdminPanel() {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('posts');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [confirmationResult, setConfirmationResult] = useState(null);
  const [loginError, setLoginError] = useState('');
  const [loginStep, setLoginStep] = useState('phone');
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [editingPost, setEditingPost] = useState(null);
  const [editingUser, setEditingUser] = useState(null);
  const [showAddPost, setShowAddPost] = useState(false);
  const [newPost, setNewPost] = useState({
    title: '',
    description: '',
    imageUrl: '',
    type: 0,
    authorPhone: '',
    postValidUntil: '',
    promotedUntil: '',
    promoDetails: {
      discount: 0,
      validUntil: ''
    },
    eventDetails: {
      location: '',
      startTime: '',
      endTime: ''
    }
  });
  const [notification, setNotification] = useState({ title: '', message: '' });
  const [showNotificationForm, setShowNotificationForm] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      setUser(currentUser);
      if (currentUser) {
        const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
        if (userDoc.exists() && userDoc.data().isAdmin) {
          setIsAdmin(true);
          loadData();
        } else {
          setIsAdmin(false);
        }
      } else {
        setIsAdmin(false);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user && !loading) {
      setupRecaptcha();
    }
    return () => {
      if (window.recaptchaVerifier) {
        window.recaptchaVerifier.clear();
        window.recaptchaVerifier = null;
      }
    };
  }, [user, loading]);

  const setupRecaptcha = () => {
    try {
      if (window.recaptchaVerifier) {
        window.recaptchaVerifier.clear();
      }

      auth.settings.appVerificationDisabledForTesting = false;

      window.recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
        size: 'normal',
        callback: (response) => {
          console.log('reCAPTCHA verified:', response);
        },
        'expired-callback': () => {
          console.log('reCAPTCHA expired');
          setupRecaptcha();
        }
      });

      window.recaptchaVerifier.render().then((widgetId) => {
        console.log('reCAPTCHA rendered with widget ID:', widgetId);
        window.recaptchaWidgetId = widgetId;
      }).catch((error) => {
        console.error('Error rendering reCAPTCHA:', error);
      });
    } catch (error) {
      console.error('Error setting up reCAPTCHA:', error);
    }
  };

  const loadData = async () => {
    try {
      const postsQuery = query(collection(db, 'posts'), orderBy('created', 'desc'));
      const postsSnapshot = await getDocs(postsQuery);
      setPosts(postsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));

      const usersSnapshot = await getDocs(collection(db, 'users'));
      setUsers(usersSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const handleSendCode = async () => {
    setLoginError('');

    if (!phoneNumber) {
      setLoginError('Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ Ð½Ð¾Ð¼ÐµÑ€ Ñ‚ÐµÐ»ÐµÑ„Ð¾Ð½Ð°');
      return;
    }

    let formattedPhone = phoneNumber.trim().replace(/\s/g, '');
    if (!formattedPhone.startsWith('+')) {
      formattedPhone = '+' + formattedPhone;
    }

    try {
      if (!window.recaptchaVerifier) {
        setupRecaptcha();
      }

      const appVerifier = window.recaptchaVerifier;
      const confirmation = await signInWithPhoneNumber(auth, formattedPhone, appVerifier);
      setConfirmationResult(confirmation);
      setLoginStep('code');
      setLoginError('');
    } catch (error) {
      console.error('Error sending code:', error);

      let errorMessage = 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ ÐºÐ¾Ð´Ð°. ';

      if (error.code === 'auth/invalid-phone-number') {
        errorMessage = 'ÐÐµÐ²ÐµÑ€Ð½Ñ‹Ð¹ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚ Ð½Ð¾Ð¼ÐµÑ€Ð° Ñ‚ÐµÐ»ÐµÑ„Ð¾Ð½Ð°. Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐ¹Ñ‚Ðµ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚: +79991234567';
      } else if (error.code === 'auth/too-many-requests') {
        errorMessage = 'Ð¡Ð»Ð¸ÑˆÐºÐ¾Ð¼ Ð¼Ð½Ð¾Ð³Ð¾ Ð¿Ð¾Ð¿Ñ‹Ñ‚Ð¾Ðº. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿Ð¾Ð·Ð¶Ðµ.';
      } else if (error.code === 'auth/quota-exceeded') {
        errorMessage = 'ÐŸÑ€ÐµÐ²Ñ‹ÑˆÐµÐ½Ð° ÐºÐ²Ð¾Ñ‚Ð° SMS. ÐžÐ±Ñ€Ð°Ñ‚Ð¸Ñ‚ÐµÑÑŒ Ðº Ð°Ð´Ð¼Ð¸Ð½Ð¸ÑÑ‚Ñ€Ð°Ñ‚Ð¾Ñ€Ñƒ.';
      } else if (error.code === 'auth/captcha-check-failed') {
        errorMessage = 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸ reCAPTCHA. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¾Ð±Ð½Ð¾Ð²Ð¸Ñ‚ÑŒ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñƒ.';
      } else {
        errorMessage += error.message;
      }

      setLoginError(errorMessage);

      setTimeout(() => {
        if (window.recaptchaVerifier) {
          window.recaptchaVerifier.clear();
          window.recaptchaVerifier = null;
        }
        setupRecaptcha();
      }, 1000);
    }
  };

  const handleVerifyCode = async () => {
    setLoginError('');

    if (!verificationCode) {
      setLoginError('Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÐºÐ¾Ð´ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ');
      return;
    }

    try {
      await confirmationResult.confirm(verificationCode);
    } catch (error) {
      console.error('Error verifying code:', error);
      setLoginError('ÐÐµÐ²ÐµÑ€Ð½Ñ‹Ð¹ ÐºÐ¾Ð´. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ ÑÐ½Ð¾Ð²Ð°.');
    }
  };

  const handleLogout = async () => {
    await signOut(auth);
    setLoginStep('phone');
    setPhoneNumber('');
    setVerificationCode('');
    setConfirmationResult(null);
  };

    const handleImageUpload = async (file) => {
      if (!file) return null;
      setUploadingImage(true);
      try {
        const fileExtension = file.name.split('.').pop();
        const fileName = `${Date.now()}_${Math.random().toString(36).substring(7)}.${fileExtension}`;
        const storageRef = ref(storage, `post_images/${fileName}`);
        await uploadBytes(storageRef, file);
        const downloadURL = await getDownloadURL(storageRef);
        setUploadingImage(false);
        return downloadURL;
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ');
        setUploadingImage(false);
        return null;
      }
    };

    const handleImageChange = async (e, isEditing = false) => {
      const file = e.target.files[0];
      if (!file) return;
      if (!file.type.startsWith('image/')) {
        alert('ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð²Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ');
        return;
      }
      const imageUrl = await handleImageUpload(file);
      if (imageUrl) {
        if (isEditing) {
          setEditingPost({ ...editingPost, imageUrl });
        } else {
          setNewPost({ ...newPost, imageUrl });
        }
      }
    };

  const handleDeletePost = async (postId) => {
    if (window.confirm('Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ ÑÑ‚Ð¾Ñ‚ Ð¿Ð¾ÑÑ‚?')) {
      await deleteDoc(doc(db, 'posts', postId));
      loadData();
    }
  };

  const handleUpdatePost = async (postId, updatedData) => {
     const dataToUpdate = {
       title: updatedData.title,
       description: updatedData.description,
       imageUrl: updatedData.imageUrl || '',
       authorPhone: updatedData.authorPhone || '',
       type: parseInt(updatedData.type),
       updated: new Date()
     };
     if (parseInt(updatedData.type) === 1 && updatedData.eventDetails) {
       dataToUpdate.eventDetails = {
         location: updatedData.eventDetails.location || '',
         startTime: updatedData.eventDetails.startTime || null,
         endTime: updatedData.eventDetails.endTime || null
       };
     } else {
       dataToUpdate.eventDetails = null;
     }
     if (parseInt(updatedData.type) === 2 && updatedData.promoDetails) {
       dataToUpdate.promoDetails = {
         discount: parseInt(updatedData.promoDetails.discount) || 0,
         validUntil: updatedData.promoDetails.validUntil || null
       };
     } else {
       dataToUpdate.promoDetails = null;
     }
     await updateDoc(doc(db, 'posts', postId), dataToUpdate);
     setEditingPost(null);
     loadData();
   };

  const handleAddPost = async () => {
     if (!newPost.title || !newPost.description) {
       alert('Ð—Ð°Ð¿Ð¾Ð»Ð½Ð¸Ñ‚Ðµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¿Ð¾Ð»Ñ');
       return;
     }
     const postData = {
       title: newPost.title,
       description: newPost.description,
       imageUrl: newPost.imageUrl || '',
       created: new Date(),
       updated: new Date(),
       authorId: user.uid,
       authorPhone: newPost.authorPhone || '',
       likeList: [],
       type: parseInt(newPost.type),
       postValidUntil: newPost.postValidUntil ? new Date(newPost.postValidUntil) : null,
       promotedUntil: newPost.promotedUntil ? new Date(newPost.promotedUntil) : null
     };
     if (parseInt(newPost.type) === 2 && newPost.promoDetails.discount > 0) {
       postData.promoDetails = {
         discount: parseInt(newPost.promoDetails.discount),
         validUntil: newPost.promoDetails.validUntil ? new Date(newPost.promoDetails.validUntil) : null
       };
     }
     if (parseInt(newPost.type) === 1 && (newPost.eventDetails.location || newPost.eventDetails.startTime)) {
       postData.eventDetails = {
         location: newPost.eventDetails.location || '',
         startTime: newPost.eventDetails.startTime ? new Date(newPost.eventDetails.startTime) : null,
         endTime: newPost.eventDetails.endTime ? new Date(newPost.eventDetails.endTime) : null
       };
     }
     await addDoc(collection(db, 'posts'), postData);
     setShowAddPost(false);
     setNewPost({
       title: '', description: '', imageUrl: '', type: 0, authorPhone: '',
       postValidUntil: '', promotedUntil: '',
       promoDetails: { discount: 0, validUntil: '' },
       eventDetails: { location: '', startTime: '', endTime: '' }
     });
     loadData();
   };

  const handleUpdateUser = async (userId, updatedData) => {
    const dataToUpdate = {
      ...updatedData,
      updated: new Date()
